function hasClass(s,e){return s.classList?s.classList.contains(e):new RegExp("\\b"+e+"\\b").test(s.className)}function addClass(s,e){s.classList?s.classList.add(e):hasClass(s,e)||(s.className+=" "+e)}function removeClass(s,e){s.classList?s.classList.remove(e):s.className=s.className.replace(new RegExp("\\b"+e+"\\b","g"),"")}function toggleClass(s,e){hasClass(s,e)?removeClass(s,e):addClass(s,e)}function escapeRegExp(s){return s.replace(/[.*+?^${}()|[\]\\]/g,"\\$&")}function capitaliseFirst(s){return s.charAt(0).toUpperCase()+s.slice(1).toLowerCase()}function getNextSibling(s,e){var a=s.nextElementSibling;if(!e)return a;for(;a;){if(a.matches(e))return a;a=a.nextElementSibling}}function getPreviousSibling(s,e){var a=s.previousElementSibling;if(!e)return a;for(;a;){if(a.matches(e))return a;a=a.previousElementSibling}}
